UPDATE `locales_quest` SET `RequestItemsText_loc3` = 'Ihr habt was getan?' WHERE `locales_quest`.`Id` = 12757; 
UPDATE `locales_quest` SET `OfferRewardText_loc3` = 'Ihr habt mir gut gedient, $N. Die Lande des Scharlachroten Kreuzzugs wurden von der Geiel gebrandmarkt. Ihr habt fr Tod und Vernichtung gesorgt, so weit das Auge reicht, un mir den letzten Rest der Armeen des Scharlachroten Kreuzzugs ans Messer geliefert.$B$B<Der Lichknig deutet auf die Ruinen von Havenau und der brennenden Stadt Neu-Avalon.>$B$BEs ist nun Zeit, dass Ihr zu Ende bringt, was Ihr begonnen habt.' WHERE `locales_quest`.`Id` = 12778; 
UPDATE `locales_quest` SET `ObjectiveText1_loc3` = 'Scharlachroter Soldat gettet', `ObjectiveText2_loc3` = 'Scharlachrote Ballisten zerstrt', `OfferRewardText_loc3` = 'Kniet nieder, Ritter. Hiermit setze ich Euch diesen Helm auf, um Euer schreckenerregendes Aussehen zu vervollkommnen. JEder, der auch nur wagen sollte, einen Blick auf Euer dunkles Antlitz zu werfen, wird wissen, dass ihn der Tod ereilen wird. Lasst niemanden es wagen, sich gegen unseren Knug zu stellen, ohne dass er Euren gnadenlosen Zorn zu spren bekme, $N.$B$BEs bleiben nur noch eine letzte Aufgabe...$B$B<Der Lichknig blickt nach Westen.>$B$BDie Kapelle des Hoffnungsvollen Lichts.' WHERE `locales_quest`.`Id` = 12779; 
